    /**
     * 全国公共资源交易平台
     * http://deal.ggzy.gov.cn/
     * 获取列表
     */
    public function getList()
    {
//        $last_day = '2020-07-05';
        $last_day = date("Y-m-d",strtotime("-1 day"));
        set_time_limit(0);
        $source_type = [1,2];
        foreach ($source_type as $vv) {
            $opt = "http://deal.ggzy.gov.cn/ds/deal/dealList_find.jsp?HEADER_DEAL_TYPE=01";
            $post = "TIMEBEGIN_SHOW={$last_day}&TIMEEND_SHOW={$last_day}&TIMEBEGIN={$last_day}&TIMEEND={$last_day}&SOURCE_TYPE={$vv}&DEAL_TIME=06&DEAL_CLASSIFY=01&DEAL_STAGE=0100&DEAL_PROVINCE=0&DEAL_CITY=0&DEAL_PLATFORM=0&BID_PLATFORM=0&DEAL_TRADE=0&isShowAll=1&PAGENUMBER=1&FINDTXT=";
            $data = curlInfo($opt, $post);
            $rs = json_decode($data);
            $page = $rs->ttlpage;//获取页数
            if (isset($rs->data)) {
                foreach ($rs->data as $v) {
                    $flag = GgzyList::create($v);
                    $this->getListUrl($flag['id'], $flag['url']);
                }
            }
            if ($page > 1) {
                for ($i = 2; $i <= $page; $i++) {
                    $opt = "http://deal.ggzy.gov.cn/ds/deal/dealList_find.jsp?HEADER_DEAL_TYPE=01";
                    $post = "TIMEBEGIN_SHOW={$last_day}&TIMEEND_SHOW={$last_day}&TIMEBEGIN={$last_day}&TIMEEND={$last_day}&SOURCE_TYPE={$vv}&DEAL_TIME=06&DEAL_CLASSIFY=01&DEAL_STAGE=0100&DEAL_PROVINCE=0&DEAL_CITY=0&DEAL_PLATFORM=0&BID_PLATFORM=0&DEAL_TRADE=0&isShowAll=1&PAGENUMBER={$i}&FINDTXT=";
                    $data = curlInfo($opt, $post);
                    $rs = json_decode($data);
                    if (isset($rs->data)) {
                        foreach ($rs->data as $v) {
                            $flag = GgzyList::create($v);
                            $this->getListUrl($flag['id'], $flag['url']);
                        }
                    }
                }
            }
        }
    }

    /**
     * 获取每项url
     * url_0101   url招标/资审公告
     * url_0102   url开标记录
     * url_0103   url待定
     * url_0104   url交易结果公示
     * url_0105   url招标/资审文件澄清
     */
    public function getListUrl($id=1,$opt=null)
    {
//        $opt = "http://www.ggzy.gov.cn/information/html/a/370000/0101/202005/27/00371d90868fea7342ff8be234ee578e0537.shtml";
        $post = '';
        $data = curlInfo($opt, $post);
        $preg= "/\/html[\s\S]*?shtml/i";
        preg_match_all($preg,$data,$matchs);
        $m = array_unique($matchs[0]);
        $i = 0;
        if ($m){
            $tmp = [];
            foreach ($m as $k => $v) {
                $v_arr = explode('/',$v);
                $tmp[$k] = [
                    'list_id' => $id,
                    'region_code' => $v_arr[3],
                    'type' => $v_arr[4],
                    'pub_time' => date('Y-m-d',strtotime($v_arr[5].$v_arr[6])),
                    'url' => $v,
                    'create_time' => time(),
                    'update_time' => time(),
                ];
                $url = "http://www.ggzy.gov.cn/information".$v;
                $p = '';
                $r = curlInfo($url, $p);
                $tmp[$k]['content'] = $r;
                $i++;
            }
            $res = Db::table('tb_ggzy_detail')->insertAll($tmp);
            if ($res){
                echo "新增 {$i} 条数据\r\n";
            }else{
                echo "操作失败\r\n";
            }
        }
    }

